main ( ) { int t , i , sum ; scanf ( "%d" , & t ) ; while ( t > 0 ) { int n ; scanf ( "%d" , & n ) ; int a [ n ] , d [ n ] ; for ( i = 1 ; i <= n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } d [ n ] = 1 ; for ( i = n - 1 ; i <= 1 ; i -- ) { if ( a [ i ] <= a [ i + 1 ] ) { d [ i ] = d [ i + 1 ] + 1 ; } else { d [ i ] = 1 ; } } sum = 0 ; for ( i = 1 ; i <= n ; i ++ ) { sum = sum + d [ i ] ; } printf ( "%d\n" , sum ) ; t -- ; } }