int gcd ( int a , int b ) { if ( b == 0 ) return a ; return gcd ( b , a % b ) ; } int main ( ) { int n ; scanf ( "%d" , & n ) ; while ( n -- ) { int x , y , l = 0 , g = 0 ; scanf ( "%d %d" , & x , & y ) ; g = gcd ( x , y ) ; l = ( x * y ) / gcd ( x , y ) ; printf ( "%d %d\n" , g , l ) ; } return 0 ; }