void lcm ( unsigned int a , unsigned int b ) { int x = a , y = b ; while ( a != b ) { if ( a > b ) a = a - b ; else b = b - a ; } printf ( "lcm is %u\n" , x * y / a ) ; } void gcd ( unsigned int a , unsigned int b ) { while ( a != b ) { if ( a > b - 1 ) a = a - b ; else b = b - a ; } printf ( "gcd is %u\n" , a ) ; } int main ( ) { unsigned int t , a , b , i ; scanf ( "%u" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%u %u" , & a , & b ) ; gcd ( a , b ) ; lcm ( a , b ) ; } }