int gcd ( int a , int b ) { if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; } int lcm ( int a , int b ) { return ( a * b ) / gcd ( a , b ) ; } int main ( ) { int a , b , t , val , val1 ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; val = gcd ( a , b ) ; val1 = lcm ( a , b ) ; printf ( "%d %d\n" , val , val1 ) ; } return 0 ; }