int main ( ) { int T ; scanf ( "%d" , & T ) ; while ( T -- ) { int n , i ; scanf ( "%d" , & n ) ; long long int A [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & A [ i ] ) ; } int c = 0 , sum = 0 , key , f = 0 ; for ( i = 0 ; i < n - 1 ; i ++ ) { key = A [ i ] ; if ( key <= A [ i + 1 ] ) { c ++ ; key = A [ i + 1 ] ; f = i ; } else { sum = sum + ( c * ( c + 1 ) ) / 2 ; c = 0 ; } } if ( f == n - 2 && c != n - 1 ) { sum = sum + ( c * ( c + 1 ) ) / 2 ; } if ( c == n - 1 ) { sum = sum + ( c * ( c + 1 ) ) / 2 ; } printf ( "%d\n" , sum + n ) ; } }