int gcd ( long long int a , long long int b ) { if ( b == 0 ) return a ; else return gcd ( b , a % b ) ; } int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int a , x , y , b ; scanf ( "%lld %lld" , & x , & y ) ; b = gcd ( x , y ) ; a = ( x * y ) / b ; printf ( "%lld %lld\n" , b , a ) ; } return 0 ; }