int gcd_fun ( int a , int b ) { int x , gcd ; if ( a > b ) { x = b ; } else { x = a ; } while ( x >= 1 ) { if ( a % x == 0 && b % x == 0 ) { gcd = x ; return gcd ; } x -- ; } return 0 ; } int main ( ) { int a , b , test , i , gc , lc ; scanf ( "%d" , & test ) ; for ( i = 0 ; i < test ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; gc = gcd_fun ( a , b ) ; lc = ( a * b ) / gc ; printf ( "%d %d\n" , gc , lc ) ; } return 0 ; }