int main ( ) { int g , l , remainder , numerator , denominator , n , i ; double a , b ; scanf ( "%d" , & n ) ; for ( i = 1 ; i <= n ; i ++ ) { scanf ( "%lf %lf" , & a , & b ) ; numerator = a ; denominator = b ; remainder = numerator % denominator ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } g = denominator ; l = a * b / g ; printf ( "%d %d\n" , g , l ) ; } return 0 ; }