void main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t > 0 ) { long long int a , b , n , min , max , t , gcd , lcm ; scanf ( "%d %d" , & a , & b ) ; if ( a > b ) { max = a ; min = b ; } else { max = b ; min = a ; } t = min ; while ( ( max % min ) != 0 ) { min = ( max % min ) ; max = t ; t = min ; } gcd = min ; if ( a > b ) { max = a ; min = b ; } else { max = b ; min = a ; } if ( ( max % min ) == 0 ) { lcm = max ; } else lcm = max * min ; printf ( "%d %d \n" , gcd , lcm ) ; t -- ; } }