int main ( ) { int a [ n ] , b , t , i , j , m , sum ; scanf ( "%d" , & t ) ; while ( t -- ) { sum = 0 ; scanf ( "%d" , & b ) ; for ( i = 0 ; i < b ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < b ; i ++ ) { m = i ; j = m + 1 ; if ( a [ i ] < a [ j ] ) { sum = sum + a [ i ] ; a [ j ] = a [ i ] ; } else if ( a [ i ] > a [ j ] ) sum = sum + a [ j ] ; } printf ( "%d\n" , sum ) ; } return 0 ; }