int main ( void ) { long long int t , n , a [ 100000 ] , i , cost ; scanf ( "%lld" , & t ) ; while ( t -- ) { cost = 0 ; scanf ( "%lld" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & a [ i ] ) ; for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) { cost = cost + a [ i + 1 ] ; } else if ( a [ i ] < a [ i + 1 ] ) { cost = cost + a [ i ] ; a [ i + 1 ] = a [ i ] ; } } printf ( "%lld\n" , cost ) ; } return 0 ; }