int main ( ) { long long int T , A , B , i , LCM ; scanf ( "%ld" , & T ) ; while ( T -- ) { scanf ( "%ld%ld" , & A , & B ) ; if ( A > B ) { if ( A % B == 0 ) { LCM = A ; } else { for ( i = 2 ; i <= 1000 ; i ++ ) { if ( ( A * i ) % B == 0 ) { LCM = A * i ; break ; } } } } else { if ( B % A == 0 ) { LCM = B ; } else { for ( i = 2 ; i <= 1000 ; i ++ ) { if ( ( B * i ) % A == 0 ) { LCM = B * i ; break ; } } } } printf ( "%ld %ld\n" , ( A * B ) / LCM , LCM ) ; LCM = 0 ; } return 0 ; }