int main ( ) { int t , gcd , remainder , keep , temp ; scanf ( "%d" , & t ) ; long long int product , lcm ; int a , b ; while ( t > 0 ) { scanf ( "%d%d" , & a , & b ) ; product = ( a * b ) ; int small = ( a < b ) ? a : b ; int big = ( a > b ) ? a : b ; if ( ( big % small ) == 0 ) gcd = small ; else { remainder = ( big % small ) ; keep = small ; while ( remainder != 0 ) { temp = remainder ; remainder = ( keep % remainder ) ; keep = temp ; } gcd = keep ; } lcm = product / ( long long int ) gcd ; printf ( "%d %lld\n" , gcd , lcm ) ; t -- ; } return 0 ; }