int main ( ) { int s , j , r , temp , k , n ; long int a [ 50000 ] ; scanf ( "%d" , & s ) ; while ( s -- ) { r = 0 ; temp = 0 ; scanf ( "%d" , & n ) ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%ld" , & a [ j ] ) ; for ( k = 0 ; k < n ; k ++ ) { if ( a [ k ] > a [ k + 1 ] ) r = r + a [ k + 1 ] ; else { temp = a [ k ] ; a [ k ] = a [ k + 1 ] ; a [ k + 1 ] = temp ; r = r + a [ k + 1 ] ; } } printf ( "%d\n" , r ) ; } return 0 ; }