int main ( void ) { long int i , x , j , T , y , A , B , k ; scanf ( "%ld" , & T ) ; for ( i = 1 ; i <= T ; i ++ ) { printf ( "\n" ) ; scanf ( "%ld %ld" , & A , & B ) ; x = A * B ; y = A > B ? B : A ; for ( k = y ; k >= 1 ; k -- ) { if ( A % k == 0 && B % k == 0 ) { printf ( "\n%ld " , k ) ; break ; } } for ( j = 1 ; j <= y ; j ++ ) { if ( A % j == 0 && B % j == 0 ) x = x / j ; } printf ( "%ld" , x ) ; } return 0 ; }