int main ( ) { int n , i , j , a [ 100000 ] , sum , b , k , f ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { sum = 0 ; scanf ( "%d" , & b ) ; for ( j = 0 ; j < ( b * ( b + 1 ) * ( b + 2 ) ) / 6 ; j ++ ) scanf ( "%d" , & a [ j ] ) ; k = a [ 0 ] ; for ( k = 0 ; k < j ; ) { if ( a [ k ] > a [ k + 1 ] ) { sum = sum + f + a [ k ] ; f = a [ k ] ; } else { sum = sum + f + a [ k + 1 ] ; f = a [ ++ k ] ; } } printf ( "%d\n" , sum ) ; } return 0 ; }