int main ( ) { int t , i , a , b , gcd , lcm , max , min ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & a , & b ) ; if ( a <= b ) { min = a ; max = b ; } else { min = b ; max = a ; } while ( 1 ) { if ( a % min == 0 && b % min == 0 ) { gcd = min ; break ; } min -- ; } while ( 1 ) { if ( max % a == 0 && max % b == 0 ) { lcm = max ; break ; } max ++ ; } printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }