typedef struct number { int A ; int B ; } number ; int gcd ( number C , int i ) { int j ; if ( C . A > C . B ) { for ( j = C . B ; j >= 1 ; j -- ) { if ( C . A % j == 0 && C . B % j == 0 ) return j ; } } else { for ( j = C . A ; j >= 1 ; j -- ) { if ( C . A % j == 0 && C . B % j == 0 ) return j ; } } } int lcm ( number C , int y , int i ) { return ( C . A ) * ( C . B ) / y ; } int main ( ) { int T , x , y , i ; scanf ( "%d" , & T ) ; number C ; for ( i = 0 ; i < T ; i ++ ) { scanf ( "%d %d" , & C . A , & C . B ) ; y = gcd ( C , i ) ; x = lcm ( C , y , i ) ; printf ( "%d %d\n" , y , x ) ; } return 0 ; }