long int gcd ( long a , long b ) { if ( b == 0 ) return a ; else return gcd ( b , a % b ) ; } long int lcm ( long int num1 , long int num2 ) { long int gcdnum = gcd ( num1 , num2 ) ; return ( num1 * num2 ) / gcdnum ; } int main ( ) { int nTestCases ; int nA , nB ; scanf ( "%d" , & nTestCases ) ; while ( nTestCases -- ) { scanf ( "%d %d" , & nA , & nB ) ; long int ll_lcm = 0 ; long int ll_gcd = 0 ; ll_lcm = lcm ( nA , nB ) ; ll_gcd = gcd ( nA , nB ) ; printf ( "%ld %ld\n" , ll_gcd , ll_lcm ) ; } return 0 ; }