int main ( ) { long long int i , j , k , n , t , count ; scanf ( "%lld" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld" , & n ) ; long long int x [ n ] ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%lld" , & x [ j ] ) ; } count = 0 ; for ( j = 0 ; j <= n - 2 ; j ++ ) { for ( k = 0 ; k <= n - j - 1 ; k ++ ) { if ( x [ j + k ] <= x [ j + k + 1 ] ) { count ++ ; } else { break ; } } } if ( n == 1 ) { printf ( "%lld\n" , count + 1 ) ; } else { printf ( "%lld\n" , count + n - 1 ) ; } } }