int lcm ( int , int ) ; int hcf ( int , int ) ; int main ( ) { int t , a , b , l , h ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; l = lcm ( a , b ) ; h = hcf ( a , b ) ; printf ( "%d\t%d\t" , h , l ) ; printf ( "\n" ) ; } return 0 ; } int lcm ( int x , int y ) { int z ; for ( z = x > y ? x : y ; z <= x * y ; z ++ ) { if ( z % x == 0 && z % y == 0 ) break ; } return ( z ) ; } int hcf ( int p , int q ) { int r ; for ( r = p < q ? p : q ; r >= 1 ; r -- ) { if ( p % r == 0 && q % r == 0 ) break ; } return ( r ) ; }