int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int n ; scanf ( "%lld" , & n ) ; long long int a [ n ] , min , i = 0 , t ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & a [ i ] ) ; min = a [ 0 ] ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i ] < min ) min = a [ i ] ; } t = min * ( n - 1 ) ; printf ( "%lld\n" , t ) ; } return 0 ; }