int main ( ) { int t ; unsigned int * a , c , i , n ; unsigned long int sum ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; a = ( unsigned int * ) malloc ( n * sizeof ( int ) ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; i = 1 ; sum = 1 ; if ( n > 1 ) sum = 0 ; while ( i < n ) { c = 1 ; while ( a [ i - 1 ] <= a [ i ] && ( i < n ) ) { i ++ ; c ++ ; } sum += ( c * ( c + 1 ) ) / 2 ; c = 1 ; i ++ ; } printf ( "%ld\n" , sum ) ; } return 0 ; }