int hcf ( int a , int b ) { if ( b == 0 ) return a ; return hcf ( b , a % b ) ; } int lcm ( int a , int b ) { return ( a * b ) / hcf ( a , b ) ; } main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; printf ( "%d %d\n" , hcf ( a , b ) , lcm ( a , b ) ) ; } }