long long gcd ( int , int ) ; int main ( ) { int t , a , b ; long long g , l ; scanf ( "%d" , & t ) ; while ( t > 0 ) { scanf ( "%d%d" , & a , & b ) ; g = gcd ( a , b ) ; printf ( "%lld " , g ) ; l = ( a * b ) / g ; printf ( "%lld\n" , l ) ; t -- ; } return ( 0 ) ; } long long gcd ( int x , int y ) { long long z ; int i ; z = 1 ; if ( x > y ) { for ( i = 2 ; i <= y ; i ++ ) { if ( x % i == 0 && y % i == 0 ) { z = z * i ; } } } else { for ( i = 2 ; i <= x ; i ++ ) { if ( x % i == 0 && y % i == 0 ) { z = z * i ; } } } return ( z ) ; }