int main ( ) { int T , i ; long long int A , B ; scanf ( "%d" , & T ) ; for ( i = 1 ; i <= T ; i ++ ) { int g , l ; scanf ( "%lld %lld" , & A , & B ) ; g = GCD ( A , B ) ; l = LCM ( A , B ) ; printf ( "%d %d\n" , g , l ) ; } return 0 ; } int GCD ( long long int a , long long int b ) { if ( b == 0 ) { return a ; } else { return GCD ( b , a % b ) ; } } int LCM ( long long int a , long long int b ) { return ( a * b ) / GCD ( a , b ) ; }