int main ( ) { int a [ 200 ] , x , n , t , m , temp , i , j ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; m = 1 ; temp = 0 ; a [ 0 ] = 1 ; for ( i = 2 ; i <= n ; i ++ ) { for ( j = 0 ; j < m ; j ++ ) { x = a [ j ] * i + temp ; a [ j ] = x ; temp = x / 10 ; } while ( temp != 0 ) { m ++ ; a [ m - 1 ] = temp ; temp = temp / 10 ; } } while ( m -- ) printf ( "%d" , a [ m ] ) ; printf ( "\n" ) ; } return 0 ; }