long long int gcdf ( long long int a , long long int b ) { long long int rem ; if ( a < b ) { a = a + b ; b = a - b ; a = a - b ; } for ( ; ( a % b ) != 0 ; ) { rem = a % b ; a = b ; b = rem ; } return b ; } long long int lcmf ( long long int a , long long int b ) { long long int i , lcm = 1 , n = b ; if ( a < b ) { a = a + b ; b = a - b ; a = a - b ; } for ( i = 2 ; i <= n ; i ++ ) { if ( a % i == 0 && b % i == 0 ) { a = a / i ; b = b / i ; lcm = lcm * i ; } } lcm = lcm * a * b ; return ( lcm ) ; } int main ( ) { long long int A , B , gcd , lcm ; int n , i ; scanf ( "%d" , & n ) ; printf ( "\n" ) ; if ( n >= 1 && n <= 1000 ) { for ( i = 1 ; i <= n ; i ++ ) { scanf ( "%lld %lld" , & A , & B ) ; if ( A >= 1 && A <= 1000000 && B >= 1 && B <= 1000000 ) { gcd = gcdf ( A , B ) ; lcm = lcmf ( A , B ) ; printf ( "\n%lld  %lld" , gcd , lcm ) ; } } } return 0 ; }