int main ( ) { int t ; long long int a , b , c , d , r , g , l ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%lld%lld" , & a , & b ) ; c = a ; d = b ; if ( b == 0 ) g = a ; else { while ( ( a % b != 0 ) ) { r = a % b ; a = b ; b = r ; } g = b ; } l = ( c * d ) / g ; printf ( "%lld %lld\n" , g , l ) ; g = l = a = b = c = d = r = 0 ; } return 0 ; }