int gcd ( int m , int n ) { if ( m == n ) { return m ; } else { if ( m > n ) return gcd ( m - n , n ) ; else return gcd ( m , n - m ) ; } } int lcm ( int m , int n ) { int x ; if ( m > n ) x = m ; else x = n ; while ( 1 ) { if ( m > n ) { if ( x % n == 0 ) { return x ; break ; } x += m ; } else { if ( x % m == 0 ) { return x ; break ; } x += n ; } } } main ( ) { int t , i , m , n ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d%d" , & m , & n ) ; printf ( "%d %d\n" , gcd ( m , n ) , lcm ( m , n ) ) ; } }