int main ( ) { int i , t , k = 0 ; long int N , count = 0 , n = 0 ; scanf ( "%d" , & t ) ; while ( t > 0 ) { scanf ( "%ld" , & N ) ; long int A [ N ] ; for ( i = 0 ; i < N ; i ++ ) { scanf ( "%ld" , & A [ i ] ) ; } for ( i = 0 ; i < N ; i ++ ) { k = i ; count = 0 ; while ( k + 1 < N ) { if ( A [ k ] <= A [ k + 1 ] ) { k ++ ; count ++ ; continue ; } else break ; } n = n + count ; } t -- ; printf ( "%ld\n" , n + N ) ; } return 0 ; }