int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n ; scanf ( "%d" , & n ) ; int arr [ n ] ; for ( int i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & arr [ i ] ) ; } int min = arr [ 0 ] ; for ( int i = 0 ; i < n ; i ++ ) { if ( arr [ i ] < min ) { min = arr [ i ] ; } } printf ( "%d\n" , ( ( n - 1 ) * min ) ) ; } }