int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t > 0 ) { long long int n , i , cnt = 0 ; scanf ( "%lld" , & n ) ; int a [ n ] ; for ( i = 1 ; i <= n ; i ++ ) scanf ( "%lld" , & a [ i ] ) ; for ( i = 1 ; i < n ; i ++ ) { if ( a [ i + 1 ] < a [ i ] ) cnt = cnt + a [ i + 1 ] ; else if ( a [ i ] <= a [ i + 1 ] ) { cnt = cnt + a [ i ] ; a [ i + 1 ] = a [ i ] ; } } printf ( "%lld\n" , cnt ) ; t -- ; } }