int main ( ) { int m , n , i , d , j , k , l ; scanf ( "%d" , & n ) ; int sum [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & d ) ; int ar [ d ] [ d ] ; for ( j = 0 ; j < d ; j ++ ) { for ( k = 0 ; k <= j ; k ++ ) scanf ( "%d" , & ar [ j ] [ k ] ) ; } sum [ i ] = ar [ 0 ] [ 0 ] ; m = 0 ; for ( l = 0 ; l < d ; l ++ ) { if ( ar [ l ] [ m ] > ar [ l ] [ m + 1 ] ) sum [ i ] = sum [ i ] + ar [ l ] [ m ] ; else { sum [ i ] = sum [ i ] + ar [ l ] [ m + 1 ] ; m = m + 1 ; } } } for ( i = 0 ; i < n ; i ++ ) ; printf ( "%d\n" , & sum [ i ] ) ; return 0 ; }