int gcd ( int a , int b ) { if ( a == 0 || b == 0 ) return 0 ; else if ( a == b ) return a ; else { if ( a > b ) return gcd ( a - b , b ) ; else return gcd ( a , b - a ) ; } } int main ( ) { int t ; long long int a , b , c , l ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%lld %lld" , & a , & b ) ; c = gcd ( a , b ) ; l = ( a * b ) / c ; printf ( "%lld %lld\n" , c , l ) ; } return 0 ; }