int main ( ) { int T ; scanf ( "%d" , & T ) ; while ( T -- ) { int m , n , i , h ; scanf ( "%d%d" , & m , & n ) ; for ( i = 1 ; i <= m * n ; i ++ ) { if ( i % m == 0 && i % n == 0 ) break ; } for ( h = m < n ? m : n ; h >= 1 ; h -- ) { if ( m % h == 0 && n % h == 0 ) break ; } printf ( "\n %d %d" , h , i ) ; } return 0 ; }