typedef long long int ll ; int main ( ) { ll a , b , t , i , c , d , k , l ; scanf ( "%lld" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%lld%lld" , & a , & b ) ; c = a > b ? a : b ; d = a < b ? a : b ; while ( c % d != 0 ) { k = d ; d = c % d ; c = k ; } l = ( a * b ) / d ; printf ( "%lld %lld\n" , d , l ) ; } }