int max ( int a , int b ) { if ( a >= b ) return a ; else return b ; } int main ( ) { int t , n , i , j , max1 ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; int a [ n + 1 ] [ n + 1 ] ; for ( i = 0 ; i < n + 1 ; i ++ ) { for ( j = 0 ; j < n + 1 ; j ++ ) { a [ i ] [ j ] = 0 ; } } for ( i = 1 ; i < n + 1 ; i ++ ) { for ( j = 1 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } for ( i = 1 ; i < n + 1 ; i ++ ) { for ( j = 1 ; j <= i ; j ++ ) { a [ i ] [ j ] += max ( a [ i - 1 ] [ j ] , a [ i - 1 ] [ j - 1 ] ) ; } } max1 = 0 ; for ( i = 0 ; i < n + 1 ; i ++ ) { if ( a [ n ] [ i ] > max1 ) max1 = a [ n ] [ i ] ; } printf ( "%d\n" , max1 ) ; } return 0 ; }