int main ( ) { int t , q ; scanf ( "%d" , & t ) ; while ( t -- ) { int n ; scanf ( "%d" , & n ) ; long long int a [ n ] ; for ( int i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; } long int sum = 0 , i = 0 ; while ( i <= n - 1 ) { int c = 1 ; while ( a [ i ] <= a [ i + 1 ] && ( i + 1 < n ) ) { c ++ ; i ++ ; } i ++ ; sum += ( c * ( c + 1 ) / 2 ) ; } printf ( "%ld" , sum ) ; } }