int main ( ) { int a [ 101 ] , b [ 101 ] , i , j , n , t , s , sum ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & n ) ; scanf ( "%d" , & a [ 0 ] ) ; if ( n == 1 ) { sum = a [ 0 ] ; printf ( "%d\n" , sum ) ; } for ( i = 1 , sum = 0 ; i < n ; ++ i ) { for ( j = 0 ; j <= i ; ++ j ) scanf ( "%d" , & b [ j ] ) ; s = a [ 0 ] + b [ 0 ] + b [ 1 ] ; if ( s > sum ) sum = s ; for ( j = 0 ; j <= i ; ++ j ) a [ j ] = b [ j ] ; } printf ( "%d\n" , sum ) ; } return 0 ; }