int main ( ) { int t , i , j , temp , n , x ; scanf ( "%d" , & t ) ; while ( t -- ) { int n ; scanf ( "%d" , & n ) ; int arr [ n ] ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%d" , & arr [ j ] ) ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j < n ; j ++ ) { if ( arr [ j ] > arr [ j + 1 ] ) { temp = arr [ j + 1 ] ; arr [ j + 1 ] = arr [ j ] ; arr [ j ] = temp ; } } } x = arr [ 0 ] * ( n - 1 ) ; printf ( "%d\n" , x ) ; } return 0 ; }