void main ( ) { int T ; scanf ( "%d" , & T ) ; while ( T -- ) { int A , B , GCD , LCM , remainder , numerator , denominator ; scanf ( "%d %d" , & A , & B ) ; if ( A > B ) { numerator = A ; denominator = B ; } else { numerator = B ; denominator = A ; } remainder = numerator % denominator ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } GCD = denominator ; LCM = A * B / GCD ; printf ( "\t%d \t%d\n" , GCD , LCM ) ; } }