int main ( ) { long long int t ; scanf ( "%lld" , & t ) ; while ( t -- ) { long long int n , i , j , k ; scanf ( "%lld" , & n ) ; long long int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%lld" , & a [ i ] ) ; } long long int c = 0 ; for ( i = 0 ; i < n - 1 ; i ++ ) { for ( j = i + 1 ; j < n ; j ++ ) { long long int s = a [ i ] ; for ( k = i + 1 ; k <= j ; k ++ ) { if ( a [ k ] < s ) break ; else s = a [ k ] ; } if ( k == j + 1 ) c ++ ; if ( a [ k ] < s ) break ; } } printf ( "%lld\n" , c + n ) ; } return 0 ; }