int main ( ) { int t ; long num1 , num2 , gcd , lcm , remainder , num , denom ; scanf ( "%d" , & t ) ; while ( t > 0 ) { scanf ( "%ld %ld" , & num1 , & num2 ) ; if ( num1 > num2 ) { num = num1 ; denom = num2 ; } else { num = num2 ; denom = num1 ; } remainder = num % denom ; while ( remainder != 0 ) { num = denom ; denom = remainder ; remainder = num % denom ; } gcd = denom ; lcm = num1 * num2 / gcd ; printf ( "%ld %ld\n" , gcd , lcm ) ; t -- ; } return 0 ; }