int main ( ) { long int t , n , a [ 500000 ] , i = 0 , j , k , sum ; scanf ( "%ld" , & t ) ; while ( i < t ) { sum = 0 ; scanf ( "%ld" , & n ) ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%ld" , & a [ j ] ) ; } for ( j = 0 , k = 1 ; k < n ; k ++ ) { if ( a [ j ] > a [ k ] ) { j = k ; sum = sum + a [ j ] ; } else sum = sum + a [ j ] ; } printf ( "%ld\n" , sum ) ; i ++ ; } return 0 ; }