int main ( ) { long long int a , b , lc , hf , c = 0 , d = 0 , i ; int t ; scanf ( "%d" , & t ) ; int arr [ 100000 ] ; while ( c < t ) { scanf ( "%lld %lld" , & a , & b ) ; lc = ( a > b ) ? a : b ; hf = ( a > b ) ? a : b ; while ( hf > 0 ) { if ( a % hf == 0 && b % hf == 0 ) { arr [ d ] = hf ; ++ d ; break ; } -- hf ; } while ( 1 ) { if ( lc % a == 0 && lc % b == 0 ) { arr [ d ] = lc ; ++ d ; break ; } ++ lc ; } ++ c ; } for ( i = 0 ; i <= d - 2 ; ++ i ) { printf ( "%d %d\n" , arr [ i ] , arr [ i + 1 ] ) ; ++ i ; } return 0 ; }