int main ( ) { int t ; scanf ( "%d" , & t ) ; int arr [ t ] , i ; for ( i = 0 ; i < t ; i ++ ) scanf ( "%d" , & arr [ i ] ) ; int j = 0 , k = 0 ; while ( k < t ) { int sum = 0 ; for ( i = 0 ; i < arr [ k ] ; i ++ ) { int flag = 1 ; int max ; for ( j = 0 ; j < i + 1 ; j ++ ) { int temp ; scanf ( "%d" , & temp ) ; if ( flag ) { max = temp ; flag = 0 ; } else { if ( max < temp ) max = temp ; } } sum = sum + max ; } for ( i = 0 ; i < t ; i ++ ) printf ( "%d\n" , sum ) ; k ++ ; } return ( 0 ) ; }