int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int n , i , min = 0 ; scanf ( "%lld" , & n ) ; long long int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%lld" , & a [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) min = a [ i + 1 ] ; else min = a [ i ] ; } printf ( "%lld\n" , ( n - 1 ) * min ) ; } return 0 ; }