int main ( ) { int t ; long long int n , i , ans , num ; long long int a [ 10000 ] ; scanf ( "%d" , & t ) ; while ( t ) { ans = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; num = 1 ; for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] <= a [ i + 1 ] ) { num = num + 1 ; } else { ans += ( num * ( num + 1 ) ) / 2 ; num = 1 ; } } num -- ; ans += ( num * ( num + 1 ) ) / 2 ; printf ( "%d\n" , ans ) ; t -- ; } return 0 ; }