long long int gcd ( long long int a , long long int b ) { long long int rem , div ; div = a / b ; rem = b ; b = a - ( div * b ) ; a = rem ; if ( b != 0 ) gcd ( a , b ) ; else return a ; } int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int gc , lcm , a , b ; scanf ( "%lld%lld" , & a , & b ) ; gc = gcd ( a , b ) ; lcm = ( a * b ) / gc ; printf ( "%lld %lld\n" , gc , lcm ) ; } }